import Form from "./formRefund/Form";
import TableRefund from "./Table/TableRefund";
import { useState } from "react";
import { nanoid } from "nanoid";

export default function RefundPage() {
	const [rows, setRows] = useState([]);
	const [formObject, setFormObject] = useState({
		month: "",
		type: "",
		dateRefund: "",
		amount: "",
		ticket: "",
	});

	const handleAddFormChange = (event) => {
		event.preventDefault();

		const fieldName = event.target.getAttribute("name");
		const fieldValue = event.target.value;

		const newFormData = { ...formObject };
		newFormData[fieldName] = fieldValue;

		setFormObject(newFormData);
	};

	function handleAddFormSubmit(event) {
		event.preventDefault();

		const newRow = {
			id: nanoid(),
			type: formObject.type,
			dateRefund: formObject.dateRefund,
			amount: formObject.amount,
			ticket: formObject.ticket === false ? (formObject.ticket = "Sì") : (formObject.ticket = "No"),
		};
		const newRows = [...rows, newRow];
		setRows(newRows);
		console.log(newRow);
		console.log(newRows);
		console.log(rows);
	}

	function translateDateTitle() {
		let year = formObject.dateRefund.slice(0, 4);
		let month = formObject.dateRefund.slice(5, 7);

		let monthArray = [
			"Gennaio",
			"Febbraio",
			"Marzo",
			"Aprile",
			"Maggio",
			"Giugno",
			"Luglio",
			"Agosto",
			"Settembre",
			"Ottobre",
			"Novembre",
			"Dicembre",
		];

		let dateTitle = monthArray[month - 1] + " " + year;
		return dateTitle;
	}

	return (
		<div className="flexbox">
			<div className="divHeader">
				<header>
					<h1 className="title">Pagina rimborso di </h1>

					<nav>
						<button className="goHome">Home</button>
					</nav>
				</header>
			</div>
			<div id="leftSide">
				<Form
					handleAddFormChange={handleAddFormChange}
					handleAddFormSubmit={handleAddFormSubmit}
					formObject={formObject}
				/>
			</div>
			<div id="rightSide">
				<div className="table">
					<TableRefund rows={rows} />
				</div>
			</div>
		</div>
	);
}
