import { maxRefundable } from "../Json/maxRefundable";

let maxTaxi = maxRefundable[0].taxi;
let maxVitto = maxRefundable[0].vitto;
let maxHotel = maxRefundable[0].hotel;
let maxTreno = maxRefundable[0].treno;

export function translateStatus(status) {
	if (status === -1) {
		return "Non approvata";
	}
	// if (status === 0) {
	//     return 'In attesa di approvazione'
	// }
	if (status === 1) {
		return "Approvata";
	}
}

export function approveStatus(ticket, amount) {
    if (ticket === "No" && amount > 10 ) {
        return -1;
    } else {
        return 1;
    }
}

export function setMaxRefundable(row) {

    // console.log(maxHotel)
    if (row.state === "Non approvata" || row.state === "In attesa di approvazione") {
        return 0;
    }
    if (row.type === "Taxi" && row.amount > maxTaxi) {
        return maxTaxi;
    }
    if (row.type === "Vitto" && row.amount > maxVitto) {
        return maxVitto;
    }
    if (row.type === "Hotel" && row.amount > maxHotel) {
        return maxHotel;
    }
    if (row.type === "Treno" && row.amount > maxTreno) {
        return maxTreno;
    }
    return row.amount;    
}
