import "./index.css";
import RefundPage from "./Pages/RefundPage";
import Header from "./Header/Header";
import { Routes, Route, useNavigate } from "react-router-dom";
import HomePage from "./Pages/HomePage";
import LoginPage from "./Pages/LoginPage";
import { useState, useMemo } from "react";
import { getRole, storageRimborsoMax } from "./API/fetchFunc";
import { UserContext } from "./UserContext";

function App() {
	const [auth, setAuth] = useState(false);
	const [user, setUser] = useState({
		id: "",
		role: "",
	});
	let navigate = useNavigate();
	const value = useMemo(() => ({ user, setAuth }), [user]);

	async function handleUsernameChange(event) {
		event.preventDefault();
		const fieldValue = event.target.value;
		let role = await getRole(fieldValue);
		console.log(role);
		setUser({ id: fieldValue, role: role });
	}

	async function login() {
		if (user.role) {
			setAuth(true);
			storageRimborsoMax();
			sessionStorage.setItem("auth", auth)
			navigate(`/home/${user.id}`);
		}
		if(!user.role){ 
		setAuth(false);
		setUser({
			id: "",
			role: "",
		})
	}
	}
	// funziona che mi controlla se esiste l'id, e se c'è me lo salva in useContext e lo setta true

	// id, ruolo usecontext
	return (
		<div>
			<UserContext.Provider value={value}>
				<Header />
				<Routes>
					<Route path="/" element={<LoginPage handleUsernameChange={handleUsernameChange} login={login} />} />
					<Route path="/refundpage/:id/:month" element={auth ? <RefundPage /> : <LoginPage />} />
					<Route path="/home/:id" element={auth ? <HomePage /> : <LoginPage />} />
				</Routes>
			</UserContext.Provider>
		</div>
	);
}

export default App;
